function randUpAndDown(n,t){return Math.round(Math.random()*t+n)-t/2}function rand(n,t){return Math.round(Math.random()*(t-n)+n)}function round2DP(n){return Math.round(100*n)/100}function round1DP(n){return Math.round(10*n)/10}function getDegrees(n){return d=n*(180/Math.PI)}function getRadians(n){return r=n*(Math.PI/180)}function getVector(n,t,r){var a=getRadians(r),e=n.x+Math.sin(a)*t,u=n.y-Math.cos(a)*t,o=new createjs.Point(e,u);return o}function getDistance(n,t){var r=n.x-t.x;r*=r;var a=n.y-t.y;return a*=a,Math.sqrt(r+a)}function getAngle(n,t){var r=t.x-n.x,a=t.y-n.y,e=Math.atan2(a,r);return e=getDegrees(e)+90,0>e&&(e=360+e),e}