function randUpAndDown(n,t){return Math.round(Math.random()*t+n)-t/2}function rand(n,t){return Math.round(Math.random()*(t-n)+n)}function round2DP(n){return Math.round(100*n)/100}function round1DP(n){return Math.round(10*n)/10}function getDegrees(n){return d=n*(180/Math.PI)}function getRadians(n){return r=n*(Math.PI/180)}function getVector(n,t,r){var e=getRadians(r),u=n.x+Math.sin(e)*t,a=n.y-Math.cos(e)*t,o=new createjs.Point(u,a);return o}function getDistance(n,t){var r=n.x-t.x;r*=r;var e=n.y-t.y;return e*=e,Math.sqrt(r+e)}function getAngle(n,t){var r=t.x-n.x,e=t.y-n.y,u=Math.atan2(e,r);return u=getDegrees(u)+90,0>u&&(u=360+u),u}function isNumber(n){return n===parseFloat(n)}function isEven(n){return isNumber(n)&&n%2==0}function isOdd(n){return isNumber(n)&&Math.abs(n)%2==1}